package main

import (
  . "github.com/onsi/ginkgo"
  . "github.com/onsi/gomega"
)

var _ = Describe("Testing Encode function", func() {
  It("Short word", func() {
    Expect(Encode("hey")).To(Equal("000111111000111000000000000111111000000111000111000111111111111000000111"))
  })
  It("Longer word", func() {
    Expect(Encode("The Sensei told me that i can do this kata")).To(Equal(""))
  })
  It("With numbers", func() {
    Expect(Encode("T3st")).To(Equal("000111000111000111000000000000111111000000111111000111111111000000111111000111111111000111000000"))
  })
  It("With special characters", func() {
    Expect(Encode("T?st!%")).To(Equal("000111000111000111000000000000111111111111111111000111111111000000111111000111111111000111000000000000111000000000000111000000111000000111000111"))
  })
})

var _ = Describe("Testing Decode function", func() {
  It("Short word", func() {
    Expect(Decode("100111111000111001000010000111111000000111001111000111110110111000010111")).To(Equal("hey"))
  })
  It("Longer word", func() {
    Expect(Decode("")).To(Equal("The Sensei told me that i can do this kata"))
  })
  It("With numbers", func() {
    Expect(Decode("000111000111000111000001000000111111000000111111000111111111000000111011000111111111000111000000")).To(Equal("T3st"))
  })
  It("With special characters", func() {
    Expect(Decode("000111000111000111000010000000111111111111011111000111111111000000111111000111101111000111000000000000111000000000000111000000111000000111000111")).To(Equal("T?st!%"))
  })
})
